head.ready(function() {

	// $(document).on("click", function(){
	// 	$(".js-popup").hide();
	// });

// init slick slider
	$('.js-slick').slick({
		dots: false,
		infinite: false,
		speed: 300,
		slidesToShow: 4,
		slidesToScroll: 4,
		arrows: true,
		adaptiveHeight: true
	});

//init datepicker
	$(".js-datepicker").datepicker();

// remove any block
	$(".js-remove").on("click",function(){
		$(this).parents(".js-removeable").addClass("is-remove-ready");
	    var timer = setTimeout(function(){
	    	$(".is-remove-ready").remove();
	    }, 300);
	    //clearTimeout(timer);
	    var counter = $(this).parents(".js-removeable-group").find(".js-removeable").length;
	 	//alert(counter);
		if (counter <= 1) {
			$(this).parents(".js-removeable-group").addClass("is-remove-ready");
			var timerGroup = setTimeout(function(){
	    		$(this).parents(".js-removeable-group").remove();
	    	}, 300); 
		}
		return false;
	}); 

	$(".js-masonry").masonry({
 		itemSelector: '.js-masonry-item',
 		columnWidth: ".masonry-sizer",
 		gutter: 20
	}); 




});